<?phpClass Usuariost extends CI_Model {    function insert($usuarios) {        $this->db->insert('usuarios', $usuarios);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    function update($usuarios, $usuario) {        $this->db->where('usuario', $usuario);        $this->db->update('usuarios', $usuarios);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    function delete($usuario) {        $this->db->where('usuario', $usuario);        $this->db->delete('usuarios');        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }function select_single($usuario) {        $this->db->where('usuario', $usuario);      return  $this->db->get('usuarios');          }    function select() {        return $this->db->get('usuarios');    }    function caja($date) {        $this->db->select('distinct(usuarios.usuario), nombre, apellido_paterno, apellido_materno, e-mail, id_rol');        $this->db->join('venta', 'venta.usuario = usuarios.usuario', 'INNER');        if ($date)            $this->db->where('date(venta.fecha) =',(new DateTime($date))->format('Y-m-d') );        $sql= $this->db->get('usuarios');    //    var_dump($this->db->last_query());                return $sql;    }    public function get_usuario($per_page, $segmento) {        $this->db->select('usuario, nombre, apellido_paterno, apellido_materno, e-mail, id_rol');        $sql = $this->db->get('usuarios', $per_page, $segmento);        return $sql->result_array();    }    public function total_filas() {        $sql = $this->db->get('usuarios');        return $sql->num_rows();    }}?>